import React from 'react';

import MovieList from './MovieList';
import SearchBar from './SearchBar';
import AddMovie from './AddMovie';

class MovieLibrary extends React.Component {
    constructor (props) {
        super (props)
        this.state = {
            searchText: '',
            bookmarkedOnly: false,
            selectedGenre: '',
            movies: props.movies
        }
        this.searchText = this.searchText.bind(this);
        this.updateMovies = this.updateMovies.bind(this);
    }
    searchText = (event) => {
        const {target} = event
        const value = target.type === 'checkbox' ? target.checked : target.value;
        this.setState({
          [target.name]: value
        }, this.updateMovies)
    }
    updateMovies = () => {
        let newList = this.state.movies.filter((movie) => movie.title.includes(this.state.searchText))
        this.setState({
            movies: newList
        })
    }
  render() {

    return (
        <div>
            <SearchBar searchText={this.searchText} bookmarkedOnly={this.searchText} selectedGenre={this.searchText} />
            <MovieList movies={this.state.movies} />
            <AddMovie />
        </div>
    );
  }
}

export default MovieLibrary;
